import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css'],
})
export class HeaderComponent implements OnInit {
  ngOnInit() {
    console.log(this.getScreenWidth)
  }

  getScreenWidth(): number {
    // Get the screen width using the window object
    const screenWidth =
      window.innerWidth ||
      document.documentElement.clientWidth ||
      document.body.clientWidth;
    return screenWidth;
  }
}
<ng-container>
  <div class="header-nav">
    <a class="zoom-link" href="" routerLinkActive="active">Home</a>
    <a class="zoom-link" href="" routerLinkActive="active">Mobiles</a>
    <a class="zoom-link" href="" routerLinkActive="active">Accessories</a>
    <a class="zoom-link" href="" routerLinkActive="active">AboutUs</a>
    <div class="auth">
      <a class="zoom-link" href="" routerLinkActive="active">Login</a>
    </div>
  </div>
</ng-container>
